	function crc16($data) {
		$crc = 0xFFFF;
		for ($i = 0; $i < strlen($data); $i++) {
			$x = (($crc >> 8) ^ ord($data[$i])) & 0xFF;
			$x ^= $x >> 4;
			$crc = (($crc << 8) ^ ($x << 12) ^ ($x << 5) ^ $x) & 0xFFFF;
		}
		return $crc;
	}

	//== Перевод из кодировки UTF8 в WIN ==============================
	function utf8_win($s){
		$out = "";
		$c1 = "";
		$byte2 = false;
		for ($c = 0; $c < strlen($s); $c ++){
			$i = ord($s[$c]);
			if ($i <= 127) $out .= $s[$c];
			if ($byte2) {
				$new_c2 = ($c1 & 3) * 64 + ($i & 63);
				$new_c1 = ($c1 >> 2) & 5;
				$new_i = $new_c1 * 256 + $new_c2;
				if ($new_i == 1025){
					$out_i = 168;
				}else {
					if ($new_i == 1105)	$out_i = 184;
					else $out_i = $new_i-848;
				}
				$out .= chr($out_i);
				$byte2 = false;
			}
			if (($i >> 5) == 6) {
				$c1 = $i;
				$byte2 = true;
			}
		}
		return $out;
	}

	//== Перевод из кодировки WIN в UTF8 ==============================
	function win_utf8($s){
		$utf = "";
		for($i = 0; $i < strlen($s); $i++){
			$donotrecode = false;
			$c = ord(substr($s, $i, 1));
			if ($c == 0xA8) $res = 0xD081;
			elseif ($c == 0xB8) $res = 0xD191;
			elseif ($c < 0xC0) $donotrecode = true;
			elseif ($c < 0xF0) $res = $c + 0xCFD0;
			else $res = $c + 0xD090;
			$c = ($donotrecode) ? chr($c) : (chr($res >> 8) . chr($res & 0xff));
			$utf .= $c;
		}
		return $utf;
	}


	function strtoupper_ru($content) {
		if ( function_exists('mb_strtoupper') ) $s = mb_strtoupper($content, 'utf-8');
		else {
			$s = $this -> utf8_win($content);
			$s = $this -> strtoupper_ansi($s);
			$s = $this -> win_utf8($s);
		}
		return $s;
	}

	function strtoupper_ansi($s){
		return strtr($s,
		"abcdefghijklmnopqrstuvwxyz".
		"\xE0\xE1\xE2\xE3\xE4\xE5".
		"\xb8\xe6\xe7\xe8\xe9\xea".
		"\xeb\xeC\xeD\xeE\xeF\xf0".
		"\xf1\xf2\xf3\xf4\xf5\xf6".
		"\xf7\xf8\xf9\xfA\xfB\xfC".
		"\xfD\xfE\xfF",
		"ABCDEFGHIJKLMNOPQRSTUVWXYZ".
		"\xC0\xC1\xC2\xC3\xC4\xC5".
		"\xA8\xC6\xC7\xC8\xC9\xCA".
		"\xCB\xCC\xCD\xCE\xCF\xD0".
		"\xD1\xD2\xD3\xD4\xD5\xD6".
		"\xD7\xD8\xD9\xDA\xDB\xDC".
		"\xDD\xDE\xDF");
	}


	function detectUTF8($s) {
		return preg_match('%(?:
		[\xC2-\xDF][\x80-\xBF]
		|\xE0[\xA0-\xBF][\x80-\xBF]
		|[\xE1-\xEC\xEE\xEF][\x80-\xBF]{2}
		|\xED[\x80-\x9F][\x80-\xBF]
		|\xF0[\x90-\xBF][\x80-\xBF]{2}
		|[\xF1-\xF3][\x80-\xBF]{3}
		|\xF4[\x80-\x8F][\x80-\xBF]{2}
		)+%xs', $s);
	}

	//== Перекодировка в UTF для AJAX =================================
	function decode($s){
		if ($this -> kod==1) $s = $this -> win_utf8($s);
		return $s;
	}

