	}

	//== Создание POST запроса ========================================
	function ManualPOST ($url, $post) {
		$parse_url = parse_url($url);
		$path = $parse_url["path"];

		$host= $parse_url["host"];
		$rez = "";

		self::$NowErr = false;
		set_error_handler(array('edost_class','my_error_handler'));

		$fp = fsockopen($host, 80, $errno, $errstr, 4); //4 - макс. время запроса

		restore_error_handler();

		if ($errno == 13) return('Err14'); //Ошибка: Настройки сервера не позволяют отправить запрос на расчет
		if (self::$NowErr) return(''); //Ошибка

		if ($fp){
			$out =	"POST ".$path." HTTP/1.0\n".
					"Host: ".$host."\n".
					"Referer: ".$url."\n".
					"Content-Type: application/x-www-form-urlencoded\n".
					"Content-Length: ".strlen($post)."\n\n".
					$post."\n\n";

			fputs($fp, $out);

			$q = 0;
			while($gets=fgets($fp,512)) {
				$rez.= $gets;
				$q++;
			}
			fclose($fp);

			if ($this->test==0) $rez=stristr($rez, "<?xml version="); //отрезаем заголовок

		}

		return($rez);
	}


	//== Функции разбора XML ==========================================
	function start_tag($parser,$name,$attrs) {
		//echo "<b>Element: $name</b><br>";	// имя элемента
		switch($name) {
			case 'tarif':	$this->curElem = array(); break;
			case 'office':	$this->curElem = array(); break;
			default:		$this->index = $name; break;
		};
	}

	function end_tag($parser,$name) {
		//echo "<b>element:: $name</b><br>"; // имя элемента
		if ((is_array($this->curElem)) && ($name=='tarif')) {
			$this->rz[] = $this ->curElem;
			$this->curElem = null;
		};
		if ((is_array($this->curElem)) && ($name=='office')) {
			$this->rz_office[] = $this->curElem; //$this->curElem['to_tarif'];
			$this->curElem = null;
		};
		$this ->index = null;
	}

	function char_data($parser,$data){
		if ((is_array($this->curElem)) && ($this->index)) {
			if (array_key_exists($this->index,$this->curElem)) $this->curElem[$this->index]=$this->curElem[$this->index].$data; else $this->curElem[$this->index] = $data;
		}
		if ( (strlen(trim($data)) > 0) and ($this->index=='stat') ) $this->status = $data;	//статус результата
		if ( (strlen(trim($data)) > 0) and ($this->index=='warning') ) $this->warning[] = $data;	//предупреждение
		//if (strlen(trim($data)) > 0) { echo 'String: '.$data.' - '.$this ->index.'<br>'; }  // выводим строку
	}


	//== Расчет стоимости доставки ====================================
	function edost_calc($to_city, $weight, $strah, $length, $width, $height, $zip='') {

		if ( !($weight>0) ) return( array('qty_company'=>0,'stat'=>11) );
		if ($to_city == '') return( array('qty_company'=>0,'stat'=>0) );

		$this->status = 0;
		$this->index = null;
		$this->rz = array();
		$this->rz_office = array();
		$this->curElem = null;
		$this->warning = array();

		$to_city_id = trim(preg_replace("/[^0-9]/i","", substr($to_city,0,50)));
		$to_city_withi = trim(preg_replace("/[^0-9ir]/i","", substr($to_city,0,50)));
		if ( ($to_city==$to_city_withi) and ($to_city_id<>'') ) {
			//Проверяем, может передали код города
			if ( ($to_city_id<1) and ($to_city_id>2279) )
				return(array('qty_company'=>0,'stat'=>0)); //Не показываем ошибку, но не считаем
		}
		else {
			//Проверяем контрольную сумму города для расчета, если не совпадает, то даже не посылаем запрос на расчет
			if (!$this->detectUTF8($to_city)) $city = $this->win_utf8($to_city);
			else $city = $to_city;

			$crc = $this->crc16($this->strtoupper_ru($city));
			if (!in_array($crc, self::$crcmas)) return(array('qty_company'=>0,'stat'=>0 )); //Не показываем ошибку, но не считаем
		}

		$to_city = str_replace(' ', '%20', $to_city);

		$xml = $this -> ManualPOST ($this->adr, "to_city=$to_city&strah=$strah&id={$this->edost_id}&p={$this->edost_pswd}&weight=$weight&ln=$length&wd=$width&hg=$height&zip=$zip");

		if ($this->test==1) echo '<br>=1======<br>('.$xml.')';

		if ($xml=='Err14') return(array('qty_company'=>0,'stat'=>14));
		if ($xml=='') return(array('qty_company'=>0,'stat'=>8));

		$code = "UTF-8"; //кодировка xml
		$this->parser = xml_parser_create($code);
		xml_set_object($this->parser,$this);
		xml_set_element_handler($this->parser,'start_tag','end_tag');
		xml_set_character_data_handler($this->parser,'char_data');
		xml_parser_set_option($this->parser,XML_OPTION_CASE_FOLDING,false); //если folding включен, то все имена тегов будут переведены в верхний регистр

		if (!xml_parse($this->parser,$xml,true)) {
			//die(sprintf('<b>Error XML: %s in line %d</b>', xml_error_string(xml_get_error_code($this->parser)),xml_get_current_line_number($this->parser)));
			return(array('qty_company'=>0,'stat'=>10));
		}
		xml_parser_free($this->parser);

		//echo '--('.$this -> status.')';
		if ($this -> status <>1) return( array('qty_company'=>0,'stat'=>$this -> status) );


		$rez = array();
		$i=0;
		$rez['qty_company']=count($this->rz);
		$rez['stat']=$this -> status;

		//предупреждения
		$rez['warning'] = $this -> warning;
		//echo "<br><pre>".print_r($this -> warning, true)."</pre>";
		//echo "<br>------<pre>".print_r($this->rz, true)."</pre>------";

		foreach($this->rz as $n){
			$i++;
			$rez['price'.$i] = preg_replace("/[^0-9.]/i","", substr(trim($n['price']),0,11) );	//цена доставки

			if (!isset($n['day'])) $n['day'] = '&nbsp;';
			$rez['day'.$i] = substr(trim($n['day']),0,60);		//срок доставки
			$rez['company'.$i] = substr($n['company'],0,80);	//название компании (в 2 раза больше из-за UTF)
			if (!isset($n['name'])) $n['name'] = '';
			$rez['name'.$i] = substr(trim($n['name']),0,80);	//название тарифа (в 2 раза больше из-за UTF)
			$rez['strah'.$i] = substr(trim($n['strah']),0,1);	//1 - со страховкой, 0 - без
			$rez['id'.$i] = substr(trim($n['id']),0,5);			//id

			if (isset($n['pickpointmap'])) $rez['pickpointmap'.$i] = preg_replace("/[^a-z_-]/i","", substr(trim($n['pickpointmap']),0,12) ); //код для отображения на карте PickPoint

			if (!isset($n['pricecash'])) $n['pricecash'] = -1;
			$rez['pricecash'.$i] = preg_replace("/[^0-9.-]/i","", substr(trim($n['pricecash']),0,11) ); //сумма доставки при наложенном платеже

			if (!isset($n['transfer'])) $n['transfer'] = 0;
			$rez['transfer'.$i] = preg_replace("/[^0-9.]/i","", substr(trim($n['transfer']),0,11) ); //сумма доплаты за наложку при получении

			for ($h = 0; $h < count($this->rz_office); $h++) {				$to_tarif = explode(',',$this->rz_office[$h]['to_tarif']); //16,18
				if ( in_array($rez['id'.$i], $to_tarif) ) {					$rez['office'.$i][] = $this->rz_office[$h];
				}
			}

			if ($this->site_win) {
				$rez['day'.$i]=$this->utf8_win($rez['day'.$i]);
				$rez['company'.$i]=$this->utf8_win($rez['company'.$i]);
				$rez['name'.$i]=$this->utf8_win($rez['name'.$i]);
				if (isset($rez['office'.$i])) {
					for ($h = 0; $h < count($rez['office'.$i]); $h++) {
						$ar_fields = array('code', 'name', 'address', 'tel', 'schedule');
						foreach ($ar_fields as $field) {
							if (isset($rez['office'.$i][$h][$field]))
								$rez['office'.$i][$h][$field] = $this->utf8_win($rez['office'.$i][$h][$field]);
						}
					}
				}
			}

			//echo '<br>====price='.$n['price'].' , id='.$n['id'].' , strah='.$n['strah'].' , day='.$n['day'].' , company='.$n['company'];

		};


		//echo "<br><pre>".print_r($this->rz_office, true)."</pre>";
		//echo "<br><pre>".print_r($rez, true)."</pre>";


		return($rez);
	}

	//== Расчет стоимости доставки по параметрам пришедшим из POST ====
	function edost_calc_post() {

		if (	(isset($_POST['edost_to_city'])) and
				(isset($_POST['edost_weight']))  and
				(isset($_POST['edost_strah']))
			) {

			if ( isset($_POST['edost_kod']) ) $this -> kod=1; else $this -> kod=0;

			$weight  = preg_replace("/[^\d.]/","",preg_replace("/,/",".", substr($_POST['edost_weight'],0,5) )); //вес в кг
			$to_city = trim(preg_replace("/[^a-z0-9а-яА-Я-(),.' ]/i","", substr($this -> utf8_win($_POST['edost_to_city']),0,100))); //город доставки
			$strah = preg_replace("/[^\d.]/","",preg_replace("/,/",".", substr($_POST['edost_strah'],0,12) )); //сумма для страховки
			if ($strah=="") $strah = 0;

			if (isset($_POST['edost_zip']))				$zip = preg_replace("/[^a-z0-9а-яА-Я-ёЁ(),.]/i","", substr($this -> utf8_win($_POST['edost_zip']),0,40)); //индекс
			else
				$zip = '';

			$length = 0;
			$width  = 0;
			$height = 0;
			if ( (isset($_POST['edost_lenght'])) and (isset($_POST['edost_width'])) and (isset($_POST['edost_height'])) ) {
				$length = preg_replace("/[^\d.]/","",preg_replace("/,/",".", substr($_POST['edost_lenght'],0,8) ));	//длина в см
				$width  = preg_replace("/[^\d.]/","",preg_replace("/,/",".", substr($_POST['edost_width'],0,8) ));	//ширина в см
				$height = preg_replace("/[^\d.]/","",preg_replace("/,/",".", substr($_POST['edost_height'],0,8) ));	//высота в см
			}

			//echo '<br>strah=',$strah.' , to_city=',win_utf8($to_city).' , weight=',$weight;

			$r = $this -> edost_calc($to_city, $weight, $strah, $length, $width, $height, $zip);

			return $r;
		}
	}

}
